import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./YourBlogs.css"; // Add CSS for styling

const YourBlogs = () => {
  const [blogs, setBlogs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/blog/yourBlogs", {
      method: "GET",
      headers: { "Content-Type": "application/json" },
      credentials: "include", // Include credentials to send the cookie
    })
      .then((response) => {
        if (response.status === 401) {
          navigate("/login"); // Redirect to login if unauthorized
          return null;
        }
        return response.json();
      })
      .then((data) => {
        if (data && data.blogs) {
          setBlogs(data.blogs); // Set the fetched blogs
        } else {
          alert("No blogs found.");
        }
      })
      .catch((error) => {
        console.error("Error fetching user blogs:", error);
        alert("Failed to load blogs. Please try again.");
      });
  }, [navigate]);

  return (
    <div className="your-blogs-container">
      <h1>Your Blogs</h1>
      {blogs.length > 0 ? (
        blogs.map((blog) => (
          <div key={blog._id} className="blog-card">
            <h2>{blog.title}</h2>
            <p>{blog.content.substring(0, 100)}...</p>
            <button onClick={() => navigate(`/blog/${blog._id}`)}>Read More</button>
          </div>
        ))
      ) : (
        <p>You have not created any blogs yet.</p>
      )}
    </div>
  );
};

export default YourBlogs;
