import React from "react";
import ReactDOM from "react-dom/client";
import { ClerkProvider } from "@clerk/clerk-react";
import App from "./App";

// Import dotenv to load environment variables
import dotenv from "dotenv";

// Load environment variables from the .env file
dotenv.config();

// Ensure that the REACT_APP_CLERK_PUBLISHABLE_KEY environment variable is loaded
const clerkFrontendApi = process.env.REACT_APP_CLERK_PUBLISHABLE_KEY;

console.log(clerkFrontendApi); // Optional: to check if the key is loaded

if (!clerkFrontendApi) {
  throw new Error("Missing Clerk Publishable Key! Please check your .env file.");
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <ClerkProvider frontendApi={clerkFrontendApi}>
    <App />
  </ClerkProvider>
);
